mapper.insert:
SELECT ORDERS_SEQ.NEXTVAL FROM DUAL
INSERT INTO ORDERS ( ORDER_MEM_NAME, ORDER_PHONE, ORDER_NUM, MEM_NUM, ORDER_PRICE, ORDER_TCOST, ORDER_DZIPCODE, ORDER_DADD1, ORDER_DADD2, ORDER_DMEMO, ORDER_PAY_NAME, ORDER_PAY_RBANK, ORDER_PAY_RACCOUNT
) VALUES ( #{ORDER_MEM_NAME}, #{ORDER_PHONE}, #{ORDER_NUM}, #{MEM_NUM}, #{ORDER_PRICE}, #{ORDER_TCOST}, #{ORDER_DZIPCODE}, #{ORDER_DADD1}, #{ORDER_DADD2}, #{ORDER_DMEMO}, #{ORDER_PAY_NAME}, #{ORDER_PAY_RBANK}, #{ORDER_PAY_RACCOUNT} )

SELECT DISTINCT ORDER_NUM FROM ORDERS WHERE MEM_NUM = #{MEM_NUM} AND ORDER_NUM = (SELECT MAX(ORDER_NUM) FROM ORDERS)
INSERT INTO ORDERS_DETAIL ( ORDER_DETAIL_NUM, ORDER_NUM, GOODS_NUM, ORDER_DETAIL_AMOUNT, ORDER_DETAIL_WEIGHT
) VALUES (ORDERS_DETAIL_SEQ.NEXTVAL, #{ORDER_NUM}, #{ORDER_DETAIL_GNUM}, #{ORDER_DETAIL_AMOUNT}, #{ORDER_DETAIL_WEIGHT}
)

SELECT MAX(ORDER_NUM) ORDER_NUM FROM ORDERS WHERE MEM_NUM= #{MEM_NUM}
INSERT INTO ADMIN_ORDER ( AORDER_NUM, AORDER_ORDER_NUM, AORDER_GOODS_NUM, MEM_NUM
) VALUES ( ADMIN_ORDER_SEQ.NEXTVAL, #{AORDER_ORDER_NUM}, #{ORDER_DETAIL_GNUM}, #{MEM_NUM} )
