mapper.select:
SELECT AAA.* FROM ( SELECT COUNT(*) OVER() AS TOTAL_COUNT, AA.*, ROWNUM AS NUM FROM ( SELECT GOODS_NUM, GOODS_NAME, GOODS_PRICE, GOODS_DATE, GOODS_CATE, GOODS_COUNT, GOODS_IMAGE from GOODS where GOODS_CATE = #{cate}
AND (GOODS_CATE LIKE '%' || #{GOODS_CATE} || '%' )
ORDER BY ${orderBy} ${orderSort}
) AA ) AAA WHERE AAA.NUM BETWEEN #{START} AND #{END}

mapper.insert:
SELECT GOODS_SEQ.NEXTVAL FROM DUAL
INSERT INTO GOODS( GOODS_NUM, GOODS_NAME, GOODS_CONTENT, GOODS_PRICE, GOODS_CATE, GOODS_IMAGE
) VALUES( #{IDX}, #{GOODS_NAME}, #{GOODS_CONTENT}, #{GOODS_PRICE}, #{GOODS_CATE}, #{GOODS_IMAGE}
)



			UPDATE GOODS_ATTRIBUTE 
				SET 
    				GOODS_ATT_AMOUNT = #{GOODS_ATT_AMOUNT},
    				GOODS_WEIGHT = #{GOODS_WEIGHT}
				WHERE GOODS_NUM = #{GOODS_NUM}

mapper.update:


UPDATE  GOODS_IMAGE
SET     GOODS_THUMBNAIL = #{GOODS_THUMBNAIL}
WHERE   GOODS_NO = #{IDX}


UPDATE  GOODS_ATTRIBUTE 
SET     GOODS_ATT_AMOUNT = GOODS_ATT_AMOUNT-#{ORDER_DETAIL_AMOUNT}
WHERE   GOODS_ATT_NO = #{GOODS_ATT_NO}
